import Foundationlet filename = "SwiftText.txt"// iOS File Path Codelet directories : [String]? = NSSearchPathForDirectoriesInDomains(NSSearchPathDirectory.DocumentDirectory, NSSearchPathDomainMask.AllDomainsMask, true) as? [String]var directory : Stringvar fullPath : String = ""if let directories = directories {    directory = directories[0] //documents directory    fullPath = directory.stringByAppendingPathComponent(filename);}    else{    println("Err: This is an error state, we could not determine the documents directory path.")    abort() // Handle this properly in your production code}var error : NSError?// First we need to write to the file// NOTE: each playground has its own temporary file system// Files created in one playground will not have the same path// as those created in anotherlet alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"alphabet.writeToFile(fullPath, atomically: false, encoding: NSUTF8StringEncoding, error: &error)if let err = error{    println("Error writing file \(fullPath): \(err.description)")}let text = String.stringWithContentsOfFile(fullPath, encoding: NSUTF8StringEncoding, error: &error)if let err = error{    println("Error reading file \(fullPath): \(err.description)")}