enum Measure{    case Cup(Double)    case Tablespoon (Double)    case Ounce (Double)    case Quantity (Int)        func convertToOunces( measure : Measure) -> Measure    {        switch (measure)        {            case let .Cup(val):                return Ounce(val * 8)            case let .Tablespoon(val):                return Ounce(val * 0.5)            default:                return measure        }    }}enum PreparationStyle{    case Whipped(String, Measure)    case Separated(String)}var sugar = Measure.Cup(2)var eggs = PreparationStyle.Whipped("Eggs", Measure.Quantity(2))

